import React, { useState, useEffect } from 'react';
import Header from './components/Header.js';
import Sidebar from './components/Sidebar.js';
import { posts } from './data/mockData.js';

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  // 다크모드 토글
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  // 다크모드 상태에 따라 body 클래스 변경
  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  }, [isDarkMode]);

  // 날짜별로 포스트 그룹화
  const today = new Date();
  const yesterday = new Date(today);
  yesterday.setDate(yesterday.getDate() - 1);
  const lastWeek = new Date(today);
  lastWeek.setDate(lastWeek.getDate() - 7);
  const lastMonth = new Date(today);
  lastMonth.setMonth(lastMonth.getMonth() - 1);

  const todayPosts = posts.slice(0, 4);
  const yesterdayPosts = posts.slice(0, 3);
  const lastWeekPosts = posts.slice(0, 2);
  const lastMonthPosts = posts.slice(0, 2);

  const ProductCard = ({ post, rank }) => {
    const [isUpvoted, setIsUpvoted] = useState(false);
    const [upvotes, setUpvotes] = useState(post.likes);

    const handleUpvote = () => {
      if (isUpvoted) {
        setUpvotes(upvotes - 1);
      } else {
        setUpvotes(upvotes + 1);
      }
      setIsUpvoted(!isUpvoted);
    };

    return (
      <div className="flex items-start space-x-4 p-4 hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors duration-200">
        {/* Rank */}
        <div className="flex-shrink-0 w-8 text-center">
          <span className="text-lg font-bold text-gray-400 dark:text-gray-500">{rank}.</span>
        </div>

        {/* Product Info */}
        <div className="flex-1 min-w-0">
          <div className="flex items-start space-x-3">
            {/* Thumbnail */}
            <div className="flex-shrink-0">
              <img
                src={post.thumbnail}
                alt={post.title}
                className="w-16 h-16 rounded-lg object-cover"
              />
            </div>

            {/* Content */}
            <div className="flex-1 min-w-0">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-1 hover:text-primary-600 dark:hover:text-primary-400 cursor-pointer">
                {post.title}
              </h3>
              <p className="text-sm text-gray-600 dark:text-gray-300 mb-2 line-clamp-2">
                {post.summary}
              </p>
              <div className="flex items-center space-x-4 text-xs text-gray-500 dark:text-gray-400">
                <span>{post.category}</span>
                <span>•</span>
                <span>{post.tags.join(' • ')}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Upvote Button */}
        <div className="flex-shrink-0">
          <button
            onClick={handleUpvote}
            className={`flex flex-col items-center space-y-1 p-2 rounded-lg transition-colors duration-200 ${
              isUpvoted 
                ? 'bg-primary-100 dark:bg-primary-900 text-primary-600 dark:text-primary-400' 
                : 'hover:bg-gray-100 dark:hover:bg-gray-800 text-gray-500 dark:text-gray-400'
            }`}
          >
            <svg className="w-5 h-5" fill={isUpvoted ? 'currentColor' : 'none'} stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
            </svg>
            <span className="text-sm font-medium">{upvotes}</span>
          </button>
        </div>

        {/* Comments */}
        <div className="flex-shrink-0 flex items-center space-x-1 text-gray-500 dark:text-gray-400">
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
          </svg>
          <span className="text-sm">{post.comments}</span>
        </div>
      </div>
    );
  };

  const SectionHeader = ({ title, subtitle, viewAllText }) => (
    <div className="flex items-center justify-between mb-4">
      <div>
        <h2 className="text-xl font-bold text-gray-900 dark:text-white">{title}</h2>
        {subtitle && <p className="text-sm text-gray-600 dark:text-gray-300 mt-1">{subtitle}</p>}
      </div>
      <button className="text-sm text-primary-600 dark:text-primary-400 hover:text-primary-700 dark:hover:text-primary-300 font-medium">
        {viewAllText}
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <Header isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />
      
      <main className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex gap-8">
          {/* Main Content */}
          <div className="flex-1">
            {/* Today's Products */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 mb-8">
              <div className="p-6">
                <SectionHeader 
                  title="오늘 출시되는 주요 제품" 
                  subtitle="오늘 가장 인기 있는 새로운 제품들을 확인하세요"
                  viewAllText="오늘 모든 제품 보기"
                />
                
                <div className="space-y-1">
                  {todayPosts.map((post, index) => (
                    <ProductCard key={post.id} post={post} rank={index + 1} />
                  ))}
                </div>

                {/* Advertisement */}
                <div className="mt-6 p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200 dark:border-green-800">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 bg-green-500 rounded-lg flex items-center justify-center">
                      <span className="text-white font-bold text-lg">B</span>
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-gray-900 dark:text-white">Launching products is hard. Managing projects shouldn't be.</h4>
                      <p className="text-sm text-gray-600 dark:text-gray-300 mt-1">Try Basecamp, the famously straightforward project management system from 37signals.</p>
                    </div>
                    <button className="px-4 py-2 bg-green-600 text-white rounded-lg text-sm font-medium hover:bg-green-700 transition-colors duration-200">
                      Sign up free
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {/* Yesterday's Products */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 mb-8">
              <div className="p-6">
                <SectionHeader 
                  title="어제의 최고 제품들" 
                  viewAllText="어제 모든 제품 보기"
                />
                
                <div className="space-y-1">
                  {yesterdayPosts.map((post, index) => (
                    <ProductCard key={`yesterday-${post.id}`} post={post} rank={index + 1} />
                  ))}
                </div>
              </div>
            </div>

            {/* Last Week's Products */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 mb-8">
              <div className="p-6">
                <SectionHeader 
                  title="지난주 최고의 제품들" 
                  viewAllText="지난주 모든 제품 보기"
                />
                
                <div className="space-y-1">
                  {lastWeekPosts.map((post, index) => (
                    <ProductCard key={`week-${post.id}`} post={post} rank={index + 1} />
                  ))}
                </div>
              </div>
            </div>

            {/* Last Month's Products */}
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700">
              <div className="p-6">
                <SectionHeader 
                  title="지난달 최고의 제품들" 
                  viewAllText="지난달 모든 제품 보기"
                />
                
                <div className="space-y-1">
                  {lastMonthPosts.map((post, index) => (
                    <ProductCard key={`month-${post.id}`} post={post} rank={index + 1} />
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="hidden lg:block w-80">
            <Sidebar isDarkMode={isDarkMode} />
          </div>
        </div>
      </main>
    </div>
  );
};

export default App;
