import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const WritePost = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    content: '',
    nickname: '',
    password: '',
    images: []
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const categories = [
    { value: 'ÏπúÍµ¨', label: 'ÏπúÍµ¨', emoji: 'üòä' },
    { value: 'Í∞ÄÏ°±', label: 'Í∞ÄÏ°±', emoji: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶' },
    { value: 'Ïù¥Ìòº', label: 'Ïù¥Ìòº', emoji: 'üò¢' },
    { value: 'Í≤∞Ìòº', label: 'Í≤∞Ìòº', emoji: 'üíí' },
    { value: 'ÎÇ®Ìé∏', label: 'ÎÇ®Ìé∏', emoji: 'üíï' },
    { value: 'ÏãúÎåÅ', label: 'ÏãúÎåÅ', emoji: 'üò§' },
    { value: 'ÏãúÎ™®', label: 'ÏãúÎ™®', emoji: 'üò§' },
    { value: 'ÏßÅÏû•', label: 'ÏßÅÏû•', emoji: 'üíº' },
    { value: 'ÎèôÎ£å', label: 'ÎèôÎ£å', emoji: 'üë•' },
    { value: 'Ï∑®ÏóÖ', label: 'Ï∑®ÏóÖ', emoji: 'üíº' },
    { value: 'ÏÑ†Î∞∞', label: 'ÏÑ†Î∞∞', emoji: 'üë®‚Äçüíº' },
    { value: 'ÌõÑÎ∞∞', label: 'ÌõÑÎ∞∞', emoji: 'üë®‚Äçüéì' },
    { value: 'Ïó¨Ïûê', label: 'Ïó¨Ïûê', emoji: 'üë©' },
    { value: 'Íµ∞ÎåÄ', label: 'Íµ∞ÎåÄ', emoji: 'üéñÔ∏è' }
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const imageFiles = files.filter(file => file.type.startsWith('image/'));
    
    if (formData.images.length + imageFiles.length > 5) {
      alert('Ïù¥ÎØ∏ÏßÄÎäî ÏµúÎåÄ 5Í∞úÍπåÏßÄ ÏóÖÎ°úÎìú Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }

    setFormData(prev => ({
      ...prev,
      images: [...prev.images, ...imageFiles]
    }));
  };

  const removeImage = (index) => {
    setFormData(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.category || !formData.title || !formData.content || !formData.nickname || !formData.password) {
      alert('Ïπ¥ÌÖåÍ≥†Î¶¨, Ï†úÎ™©, ÎÇ¥Ïö©, ÎãâÎÑ§ÏûÑ, ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (formData.password.length < 4) {
      alert('ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 4Ïûê Ïù¥ÏÉÅ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setIsSubmitting(true);

    try {
      // Ïã§Ï†ú Íµ¨ÌòÑÏóêÏÑúÎäî API Ìò∏Ï∂ú
      console.log('Í≤åÏãúÍ∏Ä ÏûëÏÑ±:', formData);

      // ÏÑ±Í≥µ Ïãú Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      setTimeout(() => {
        alert('Í≤åÏãúÍ∏ÄÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏûëÏÑ±ÎêòÏóàÏäµÎãàÎã§!');
        navigate('/');
      }, 1000);
    } catch (error) {
      alert('Í≤åÏãúÍ∏Ä ÏûëÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    if (formData.title || formData.content) {
      if (window.confirm('ÏûëÏÑ± Ï§ëÏù∏ ÎÇ¥Ïö©Ïù¥ ÏûàÏäµÎãàÎã§. Ï†ïÎßê ÎÇòÍ∞ÄÏãúÍ≤†ÏäµÎãàÍπå?')) {
        navigate('/');
      }
    } else {
      navigate('/');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Ìó§Îçî */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={handleCancel}
                className="flex items-center space-x-2 text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
                <span className="text-sm font-medium">Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</span>
              </button>
            </div>
            <h1 className="text-2xl font-bold text-gray-900 dark:text-white">ÏÉà Í≤åÏãúÍ∏Ä ÏûëÏÑ±</h1>
            <div className="w-32"></div> {/* Í∑†ÌòïÏùÑ ÏúÑÌïú Îπà Í≥µÍ∞Ñ */}
          </div>
        </div>

        {/* Í∏ÄÏì∞Í∏∞ Ìèº */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700">
          <form onSubmit={handleSubmit} className="p-6">
            {/* Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑ†ÌÉù */}
            <div className="mb-6">
              <div className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
                Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑ†ÌÉù *
              </div>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
                {categories.map((category) => (
                  <button
                    key={category.value}
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, category: category.value }))}
                    className={`p-3 rounded-lg border-2 transition-all duration-200 ${
                      formData.category === category.value
                        ? 'border-primary-500 bg-primary-50 dark:bg-primary-900/20 text-primary-700 dark:text-primary-300'
                        : 'border-gray-200 dark:border-gray-600 hover:border-gray-300 dark:hover:border-gray-500 text-gray-700 dark:text-gray-300'
                    }`}
                  >
                    <div className="flex items-center space-x-2">
                      <span className="text-lg">{category.emoji}</span>
                      <span className="text-sm font-medium">{category.label}</span>
                    </div>
                  </button>
                ))}
              </div>
            </div>

            {/* ÎãâÎÑ§ÏûÑ ÏûÖÎ†• */}
            <div className="mb-6">
              <label htmlFor="nickname" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                ÎãâÎÑ§ÏûÑ *
              </label>
              <input
                type="text"
                id="nickname"
                name="nickname"
                value={formData.nickname}
                onChange={handleInputChange}
                placeholder="ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:border-primary-500 dark:bg-gray-700 dark:text-white transition-all duration-200"
                maxLength={20}
              />
              <div className="flex justify-between items-center mt-1">
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  Í≤åÏãúÍ∏ÄÏóê ÌëúÏãúÎê† ÎãâÎÑ§ÏûÑÏûÖÎãàÎã§
                </span>
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  {formData.nickname.length}/20
                </span>
              </div>
            </div>

            {/* ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†• */}
            <div className="mb-6">
              <label htmlFor="password" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                ÎπÑÎ∞ÄÎ≤àÌò∏ *
              </label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî (4Ïûê Ïù¥ÏÉÅ)"
                className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:border-primary-500 dark:bg-gray-700 dark:text-white transition-all duration-200"
                minLength={4}
                maxLength={20}
              />
              <div className="flex justify-between items-center mt-1">
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  Í≤åÏãúÍ∏Ä ÏàòÏ†ï/ÏÇ≠Ï†ú Ïãú ÌïÑÏöîÌï©ÎãàÎã§
                </span>
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  {formData.password.length}/20
                </span>
              </div>
            </div>

            {/* Ï†úÎ™© ÏûÖÎ†• */}
            <div className="mb-6">
              <label htmlFor="title" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Ï†úÎ™© *
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Í≤åÏãúÍ∏Ä Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:border-primary-500 dark:bg-gray-700 dark:text-white transition-all duration-200"
                maxLength={100}
              />
              <div className="flex justify-between items-center mt-1">
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  ÏùµÎ™ÖÏúºÎ°ú ÏûëÏÑ±Îê©ÎãàÎã§
                </span>
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  {formData.title.length}/100
                </span>
              </div>
            </div>

            {/* ÎÇ¥Ïö© ÏûÖÎ†• */}
            <div className="mb-6">
              <label htmlFor="content" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                ÎÇ¥Ïö© *
              </label>
              <textarea
                id="content"
                name="content"
                value={formData.content}
                onChange={handleInputChange}
                placeholder="Í≤åÏãúÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                rows={12}
                className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:border-primary-500 dark:bg-gray-700 dark:text-white resize-none transition-all duration-200"
                maxLength={2000}
              />
              <div className="flex justify-between items-center mt-1">
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  Í∞úÏù∏Ï†ïÎ≥¥ÎÇò ÌÉÄÏù∏ÏùÑ ÎπÑÎ∞©ÌïòÎäî ÎÇ¥Ïö©ÏùÄ ÏÇºÍ∞ÄÌï¥Ï£ºÏÑ∏Ïöî
                </span>
                <span className="text-xs text-gray-500 dark:text-gray-400">
                  {formData.content.length}/2000
                </span>
              </div>
            </div>

            {/* Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú */}
            <div className="mb-8">
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Ïù¥ÎØ∏ÏßÄ Ï≤®Î∂Ä (ÏÑ†ÌÉùÏÇ¨Ìï≠)
              </label>
              <div className="border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg p-6 text-center">
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                  id="image-upload"
                />
                <label htmlFor="image-upload" className="cursor-pointer">
                  <svg className="mx-auto h-12 w-12 text-gray-400" stroke="currentColor" fill="none" viewBox="0 0 48 48">
                    <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02" strokeWidth={2} strokeLinecap="round" strokeLinejoin="round" />
                  </svg>
                  <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                    <span className="font-medium text-primary-600 dark:text-primary-400 hover:text-primary-500">ÌÅ¥Î¶≠ÌïòÏó¨ Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú</span>
                  </p>
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    PNG, JPG, GIF ÏµúÎåÄ 5Í∞ú (Í∞Å 5MB Ïù¥Ìïò)
                  </p>
                </label>
              </div>

              {/* ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ ÎØ∏Î¶¨Î≥¥Í∏∞ */}
              {formData.images.length > 0 && (
                <div className="mt-4">
                  <h4 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ</h4>
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
                    {formData.images.map((image, index) => (
                      <div key={index} className="relative group">
                        <img
                          src={URL.createObjectURL(image)}
                          alt={`ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ ${index + 1}`}
                          className="w-full h-24 object-cover rounded-lg"
                        />
                        <button
                          type="button"
                          onClick={() => removeImage(index)}
                          className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs hover:bg-red-600 transition-colors duration-200"
                        >
                          √ó
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Î≤ÑÌäº Í∑∏Î£π */}
            <div className="flex items-center justify-end space-x-4 pt-6 border-t border-gray-200 dark:border-gray-700">
              <button
                type="button"
                onClick={handleCancel}
                className="px-6 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-600 transition-colors duration-200"
              >
                Ï∑®ÏÜå
              </button>
              <button
                type="submit"
                disabled={isSubmitting || !formData.category || !formData.title || !formData.content || !formData.nickname || !formData.password}
                className="px-6 py-2 text-sm font-medium text-white bg-primary-600 rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200 flex items-center space-x-2"
              >
                {isSubmitting ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    <span>ÏûëÏÑ± Ï§ë...</span>
                  </>
                ) : (
                  <>
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                    </svg>
                    <span>Í≤åÏãúÍ∏Ä ÏûëÏÑ±</span>
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default WritePost;
