import React from 'react';
import { categories } from '../data/mockData.js';

const Sidebar = () => {
  return (
    <aside className="w-64 flex-shrink-0">
      <div className="sticky top-20">
        {/* Categories */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">카테고리</h3>
          <div className="space-y-2">
            {categories.map((category) => (
              <a
                key={category.id}
                href={`/category/${category.slug}`}
                className="flex items-center justify-between p-3 rounded-lg hover:bg-gray-50 transition-colors duration-200 group"
              >
                <div className="flex items-center space-x-3">
                  <div className={`w-3 h-3 rounded-full ${category.color}`}></div>
                  <span className="text-sm font-medium text-gray-700 group-hover:text-gray-900">
                    {category.name}
                  </span>
                </div>
                <svg className="w-4 h-4 text-gray-400 group-hover:text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </a>
            ))}
          </div>
        </div>

        {/* Trending Tags */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">인기 태그</h3>
          <div className="flex flex-wrap gap-2">
            {['React', 'JavaScript', 'TypeScript', 'UI/UX', '마케팅', '스타트업', '생산성', '개발'].map((tag) => (
              <span
                key={tag}
                className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-700 hover:bg-gray-200 transition-colors duration-200 cursor-pointer"
              >
                {tag}
              </span>
            ))}
          </div>
        </div>

        {/* Community Stats */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">커뮤니티 현황</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">총 게시글</span>
              <span className="text-sm font-semibold text-gray-900">1,234</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">총 회원</span>
              <span className="text-sm font-semibold text-gray-900">5,678</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">오늘 게시글</span>
              <span className="text-sm font-semibold text-primary-600">23</span>
            </div>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
