import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { posts } from '../data/mockData.js';

const PostDetail = ({ postId }) => {
  const navigate = useNavigate();
  const [commentText, setCommentText] = useState('');
  const [comments, setComments] = useState([
    {
      id: 1,
      author: 'ÍπÄÎåìÍ∏Ä',
      content: 'Ï†ïÎßê ÎèÑÏõÄÏù¥ ÎêòÎäî Í∏ÄÏù¥ÎÑ§Ïöî! Ï†ÄÎèÑ ÎπÑÏä∑Ìïú Í≤ΩÌóòÏù¥ ÏûàÏñ¥ÏÑú Í≥µÍ∞êÎê©ÎãàÎã§.',
      createdAt: '2024-01-15 14:30',
      likes: 5
    },
    {
      id: 2,
      author: 'Ïù¥ÎåìÍ∏Ä',
      content: 'Ï¢ãÏùÄ Ï†ïÎ≥¥ Í∞êÏÇ¨Ìï©ÎãàÎã§. Îçî ÏûêÏÑ∏Ìïú ÎÇ¥Ïö©Ïù¥ Í∂ÅÍ∏àÌï¥Ïöî.',
      createdAt: '2024-01-15 15:20',
      likes: 3
    },
    {
      id: 3,
      author: 'Î∞ïÎåìÍ∏Ä',
      content: 'Ïù¥Îü∞ ÏÉÅÌô©ÏóêÏÑú Ïñ¥ÎñªÍ≤å ÎåÄÏ≤òÌïòÏÖ®ÎÇòÏöî? Ï°∞Ïñ∏ Î∂ÄÌÉÅÎìúÎ†§Ïöî.',
      createdAt: '2024-01-15 16:10',
      likes: 7
    }
  ]);

  // Í≤åÏãúÍ∏Ä Ï∞æÍ∏∞
  const post = posts.find(p => p.id === postId);

  if (!post) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">Í≤åÏãúÍ∏ÄÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§</h2>
          <p className="text-gray-600 dark:text-gray-300">ÏöîÏ≤≠ÌïòÏã† Í≤åÏãúÍ∏ÄÏù¥ Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏäµÎãàÎã§.</p>
        </div>
      </div>
    );
  }

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (commentText.trim()) {
      const newComment = {
        id: comments.length + 1,
        author: 'ÎÇò',
        content: commentText,
        createdAt: new Date().toLocaleString('ko-KR'),
        likes: 0
      };
      setComments([newComment, ...comments]);
      setCommentText('');
    }
  };

  const handleLikeComment = (commentId) => {
    setComments(comments.map(comment => 
      comment.id === commentId 
        ? { ...comment, likes: comment.likes + 1 }
        : comment
    ));
  };

  // Í¥ÄÎ†® Í≤åÏãúÍ∏Ä (Í∞ôÏùÄ Ïπ¥ÌÖåÍ≥†Î¶¨)
  const relatedPosts = posts
    .filter(p => p.category === post.category && p.id !== post.id)
    .slice(0, 3);

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Îí§Î°úÍ∞ÄÍ∏∞ Î≤ÑÌäº */}
        <div className="mb-6">
          <button
            onClick={() => navigate('/')}
            className="flex items-center space-x-2 text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
            <span className="text-sm font-medium">Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</span>
          </button>
        </div>

        {/* Í≤åÏãúÍ∏Ä Ìó§Îçî */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 mb-8">
          <div className="p-6">
            {/* Ïπ¥ÌÖåÍ≥†Î¶¨ Î∞è ÏûëÏÑ±Ïûê Ï†ïÎ≥¥ */}
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-gray-200 rounded-full flex items-center justify-center">
                  <span className="text-xl">
                    {post.category === 'ÏãúÎåÅ' && 'üò§'}
                    {post.category === 'ÏπúÍµ¨' && 'üòä'}
                    {post.category === 'Ïù¥Ìòº' && 'üò¢'}
                    {post.category === 'ÎÇ®Ìé∏' && 'üíï'}
                    {post.category === 'ÎèôÎ£å' && 'üë•'}
                  </span>
                </div>
                <div>
                  <span className="text-sm font-medium text-gray-600 dark:text-gray-400">{post.category}</span>
                  <div className="flex items-center space-x-2">
                    <span className="text-sm text-gray-500 dark:text-gray-400">ÏûëÏÑ±Ïûê: {post.author.name}</span>
                    <span className="text-sm text-gray-400 dark:text-gray-500">‚Ä¢</span>
                    <span className="text-sm text-gray-400 dark:text-gray-500">{post.createdAt}</span>
                  </div>
                </div>
              </div>
              <div className="flex items-center space-x-4">
                <button className="flex items-center space-x-1 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200">
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
                  </svg>
                  <span className="text-sm">Í≥µÏú†</span>
                </button>
                <button className="flex items-center space-x-1 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200">
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z" />
                  </svg>
                  <span className="text-sm">Ï†ÄÏû•</span>
                </button>
              </div>
            </div>

            {/* Ï†úÎ™© */}
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
              {post.title}
            </h1>

            {/* Ïç∏ÎÑ§Ïùº Ïù¥ÎØ∏ÏßÄ */}
            <div className="mb-6">
              <img 
                src={post.thumbnail} 
                alt={post.title}
                className="w-full h-64 object-cover rounded-lg"
              />
            </div>

            {/* ÎÇ¥Ïö© */}
            <div className="prose prose-lg max-w-none text-gray-700 dark:text-gray-300 mb-6">
              <p className="text-lg leading-relaxed">
                {post.summary}
              </p>
              <p className="text-lg leading-relaxed mt-4">
                Ïù¥ Í∏ÄÏóêÏÑúÎäî {post.category}ÏôÄ Í¥ÄÎ†®Îêú Îã§ÏñëÌïú ÏÉÅÌô©Í≥º Ìï¥Í≤∞ Î∞©Î≤ïÏóê ÎåÄÌï¥ ÏûêÏÑ∏Ìûà Îã§Î£®Í≥† ÏûàÏäµÎãàÎã§. 
                ÎßéÏùÄ Î∂ÑÎì§Ïù¥ ÎπÑÏä∑Ìïú Í≥†ÎØºÏùÑ ÌïòÍ≥† Í≥ÑÏã§ ÌÖêÎç∞, Ïù¥ Í∏ÄÏù¥ ÎèÑÏõÄÏù¥ ÎêòÏóàÏúºÎ©¥ Ìï©ÎãàÎã§.
              </p>
              <p className="text-lg leading-relaxed mt-4">
                Ïã§Ï†ú Í≤ΩÌóòÏùÑ Î∞îÌÉïÏúºÎ°ú ÏûëÏÑ±Ìïú Í∏ÄÏù¥ÎØÄÎ°ú, Ïó¨Îü¨Î∂ÑÏùò ÏÉÅÌô©ÏóêÎèÑ Ï†ÅÏö©Ìï¥Î≥¥ÏãúÍ∏∞ Î∞îÎûçÎãàÎã§. 
                Í∂ÅÍ∏àÌïú Ï†êÏù¥ÎÇò Ï∂îÍ∞ÄÎ°ú Í∂ÅÍ∏àÌïú ÎÇ¥Ïö©Ïù¥ ÏûàÏúºÏãúÎ©¥ ÎåìÍ∏ÄÎ°ú ÎÇ®Í≤®Ï£ºÏÑ∏Ïöî.
              </p>
            </div>

            {/* ÌÉúÍ∑∏ */}
            <div className="flex flex-wrap gap-2 mb-6">
              {post.tags.map((tag, index) => (
                <span 
                  key={index}
                  className="px-3 py-1 text-sm font-medium text-gray-600 dark:text-gray-300 bg-gray-100 dark:bg-gray-700 rounded-full"
                >
                  #{tag}
                </span>
              ))}
            </div>

            {/* Ïï°ÏÖò Î≤ÑÌäºÎì§ */}
            <div className="flex items-center justify-between pt-6 border-t border-gray-200 dark:border-gray-700">
              <div className="flex items-center space-x-6">
                <button className="flex items-center space-x-2 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
                  </svg>
                  <span className="text-sm font-medium">{post.likes}</span>
                </button>
                <button className="flex items-center space-x-2 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                  </svg>
                  <span className="text-sm font-medium">{comments.length}</span>
                </button>
              </div>
              <div className="flex items-center space-x-4">
                <button className="text-sm text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200">
                  Ïã†Í≥†
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* ÎåìÍ∏Ä ÏÑπÏÖò */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 mb-8">
          <div className="p-6">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-6">ÎåìÍ∏Ä ({comments.length})</h3>
            
            {/* ÎåìÍ∏Ä ÏûëÏÑ± Ìèº */}
            <form onSubmit={handleCommentSubmit} className="mb-8">
              <div className="flex space-x-4">
                <div className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center flex-shrink-0">
                  <span className="text-sm font-medium text-gray-600">ÎÇò</span>
                </div>
                <div className="flex-1">
                  <textarea
                    value={commentText}
                    onChange={(e) => setCommentText(e.target.value)}
                    placeholder="ÎåìÍ∏ÄÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî..."
                    className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:bg-gray-700 dark:text-white"
                    rows="3"
                  />
                  <div className="flex justify-end mt-2">
                    <button
                      type="submit"
                      disabled={!commentText.trim()}
                      className="px-4 py-2 bg-primary-600 text-white rounded-lg text-sm font-medium hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200"
                    >
                      ÎåìÍ∏Ä ÏûëÏÑ±
                    </button>
                  </div>
                </div>
              </div>
            </form>

            {/* ÎåìÍ∏Ä Î™©Î°ù */}
            <div className="space-y-6">
              {comments.map((comment) => (
                <div key={comment.id} className="flex space-x-4">
                  <div className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center flex-shrink-0">
                    <span className="text-sm font-medium text-gray-600">{comment.author.charAt(0)}</span>
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <span className="text-sm font-medium text-gray-900 dark:text-white">{comment.author}</span>
                      <span className="text-sm text-gray-400 dark:text-gray-500">{comment.createdAt}</span>
                    </div>
                    <p className="text-gray-700 dark:text-gray-300 mb-2">{comment.content}</p>
                    <div className="flex items-center space-x-4">
                      <button 
                        onClick={() => handleLikeComment(comment.id)}
                        className="flex items-center space-x-1 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5" />
                        </svg>
                        <span className="text-sm">{comment.likes}</span>
                      </button>
                      <button className="text-sm text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200">
                        ÎãµÍ∏Ä
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Í¥ÄÎ†® Í≤åÏãúÍ∏Ä */}
        {relatedPosts.length > 0 && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700">
            <div className="p-6">
              <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-6">Í¥ÄÎ†® Í≤åÏãúÍ∏Ä</h3>
