  const handleSelectItem = (itemId, type) => {
    setSelectedItems(prev => {
      const key = `${type}-${itemId}`;
      if (prev.includes(key)) {
        return prev.filter(id => id !== key);
      } else {
        return [...prev, key];
      }
    });
